import React from 'react';
import { styles } from './styles';
import { Link } from "react-router-dom";

const SelectRoom = ({ setRoom, nextStep, rooms }) => {
    const handleRoomSelection = (room) => {
        setRoom(room);
        nextStep();
    };

    return (
        <div>
            <h2>Select a Room</h2>
            {/* <button onClick={() => handleRoomSelection('Room 1')}>Room 1</button>
            <button onClick={() => handleRoomSelection('Room 2')}>Room 2</button> */}
            {rooms.map(room => (
                <Link 
                    to={`/room/${room.id}`} 
                    style={styles.roomLink}
                    key={room.id} // move key here to avoid React warning
                >
                    <div 
                        style={{ 
                            ...styles.roomItem, 
                            ...(hoveredRoomId === room.id ? styles.roomItemHover : {}) 
                        }}
                        onMouseEnter={() => setHoveredRoomId(room.id)}
                        onMouseLeave={() => setHoveredRoomId(null)}
                    >
                        <span style={styles.roomInfo}>{room.name}</span>
                    </div>
                </Link>
            ))}
        </div>


    );
};

export default SelectRoom;
