import React, { useEffect, useState } from 'react';
import seatService from '../../../services/seatService';
import { styles } from './styles';

const SeatSelector = ({ x, y, seats, selectedSeat, handleSeatClick }) => {
    const seatElements = [];

    for (let row = 0; row < x; row++) {
        for (let col = 0; col < y; col++) {
            const seatId = `${row}-${col}`;
            const seat = seats.find(s => s.id === seatId);
            seatElements.push(
                <div
                    key={seatId}
                    style={{
                        ...styles.seatItem,
                        backgroundColor: seat?.isBooked ? 'red' : 'green',
                        borderColor: selectedSeat?.id === seatId ? 'blue' : 'transparent',
                    }}
                    onClick={() => handleSeatClick(seat)}
                >
                    {seatId}
                </div>
            );
        }
    }

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Select a Seat</h2>
            {seats.length === 0 ? (
                <p style={styles.noSeatsMessage}>No available seat was found</p>
            ) : (
                <div style={styles.seatGrid}>
                    {seatElements}
                </div>
            )}
        </div>
    );
};

export default SeatSelector;
