export const styles = {
    container: {
        padding: '20px',
    },
    title: {
        fontSize: '24px',
        marginBottom: '20px',
    },
    noSeatsMessage: {
        fontSize: '18px',
        color: 'red',
    },
    seatGrid: {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(50px, 1fr))',
        gap: '10px',
    },
    seatItem: {
        width: '50px',
        height: '50px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        border: '2px solid transparent',
        cursor: 'pointer',
        color: 'white',
    },
    button: {
        marginTop: '20px',
    },
};
