import React, { useEffect, useState } from 'react';
import timeSlotService from '../../../services/timeSlotService';
import moment from 'moment';

const SelectTimeSlot = ({ setTimeSlot, prevStep, room, film }) => {
    const [timeSlots, setTimeSlots] = useState([]);

    useEffect(() => {
        if (room && film) {
            fetchTimeSlots(room, film);
        }
    }, [room, film]);

    const fetchTimeSlots = async (room, film) => {
        try {
            console.log(room)
            const timeSlotsData = await timeSlotService.getAvaiableTimeSlots(room.id, film.id);
            console.log(timeSlotsData)
            setTimeSlots(timeSlotsData);
        } catch (error) {
            console.error('Error fetching time slots:', error);
            setTimeSlots([]);
        }
    };

    const handleTimeSlotSelection = (timeSlot) => {
        setTimeSlot(timeSlot);
    };

    return (
        <div>
            <h2>Select a Time Slot</h2>
            {timeSlots.length === 0 ? (
                <p>No avaiable time slot was found</p>
            ) : (
                <div>
                     {Object.keys(timeSlots).map((date) => (
                        <div key={date}>
                        <h3>{date}</h3>
                        <ul>
                            {timeSlots[date].map((item, index) => (
                            <li key={index}>
                                Hour: {item[1]}, ID: {item[1]}
                            </li>
                            ))}
                        </ul>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default SelectTimeSlot;
