import React, { useState } from 'react';
import { styles } from './styles';

const SeatSelector = ({ seats, selectedSeat, handleSeatClick }) => {
    // Assuming seats are available based on a boolean array
    const [seatAvailability, setSeatAvailability] = useState(Array(6).fill(Array(8).fill(true))); // Initially all seats are available

    // Function to toggle seat availability (for demonstration purposes)
    const toggleSeatAvailability = (row, col) => {
        const updatedAvailability = [...seatAvailability];
        updatedAvailability[row][col] = !updatedAvailability[row][col];
        setSeatAvailability(updatedAvailability);
    };

    const seatElements = [];

    for (let row = 0; row < 6; row++) { // 6 rows
        for (let col = 0; col < 8; col++) { // 8 columns
            const seat = { row, col };
            const isAvailable = seatAvailability[row][col];
            const backgroundColor = isAvailable ? 'green' : 'red'; // Green for available, red for booked

            seatElements.push(
                <div
                    key={`${row}-${col}`}
                    style={{
                        ...styles.seatItem,
                        backgroundColor,
                        borderColor: selectedSeat?.row === row && selectedSeat?.col === col ? 'blue' : 'transparent',
                        cursor: isAvailable ? 'pointer' : 'not-allowed', // Show pointer only for available seats
                    }}
                    onClick={() => isAvailable && handleSeatClick(seat)}
                >
                    {`${row}-${col}`}
                </div>
            );
        }
    }

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Select a Seat</h2>
            <div style={styles.seatGrid}>
                {seatElements}
            </div>
        </div>
    );
};

export default SeatSelector;
