import React, { useEffect, useState } from 'react';
import roomService from '../../../../services/roomService';
import { useParams } from 'react-router-dom';
import TopBar from '../../TopBar/TopBar';

function RoomPage() {
    const { roomId } = useParams();
    const [room, setRoom] = useState(null);
    const [timeSlots, setTimeSlots] = useState([]);
    const [newTimeSlot, setNewTimeSlot] = useState({
        startTime: '',
        endTime: ''
    });

    useEffect(() => {
        async function fetchRoom() {
            const data = await roomService.getRoomById(roomId);
            setRoom(data);
            setTimeSlots(data.timeSlots); // Assuming room object has a timeSlots array
        }
        fetchRoom();
    }, [roomId]);

    // Function to handle form input changes
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewTimeSlot({ ...newTimeSlot, [name]: value });
    };

    // Function to add a new time slot
    const addTimeSlot = () => {
        // Assuming roomService method for adding time slot
        roomService.addTimeSlot(roomId, newTimeSlot).then((newTimeSlot) => {
            setTimeSlots([...timeSlots, newTimeSlot]);
            setNewTimeSlot({ startTime: '', endTime: '' });
        });
    };

    // Function to delete a time slot
    const deleteTimeSlot = (timeSlotId) => {
        // Assuming roomService method for deleting time slot
        roomService.deleteTimeSlot(roomId, timeSlotId).then(() => {
            setTimeSlots(timeSlots.filter(slot => slot.id !== timeSlotId));
        });
    };

    if (!room) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <TopBar />
            <div>
                <h2>{room.name}</h2>
                <p>Room Details: {room.details}</p>

                {/* Form to add new time slot */}
                <form onSubmit={addTimeSlot}>
                    <input
                        type="time"
                        name="startTime"
                        value={newTimeSlot.startTime}
                        onChange={handleInputChange}
                    />
                    <input
                        type="time"
                        name="endTime"
                        value={newTimeSlot.endTime}
                        onChange={handleInputChange}
                    />
                    <button type="submit">Add Time Slot</button>
                </form>

                {/* Display existing time slots */}
                <div>
                    <h3>Time Slots:</h3>
                    <ul>
                        {timeSlots.map((slot) => (
                            <li key={slot.id}>
                                {slot.startTime} - {slot.endTime}
                                <button onClick={() => deleteTimeSlot(slot.id)}>Delete</button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default RoomPage;
