// styles.js
export const styles = {
    form: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '20px',
        padding: '30px',
        border: '1px solid #ddd',
        borderRadius: '10px',
        backgroundColor: '#fff',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        maxWidth: '500px',
        margin: '50px auto',
    },
    input: {
        width: '100%',
        border: '1px solid hsl(0, 0%, 80%)',
        padding: 8,
        borderRadius: 8,
        color: 'hsl(0, 0%, 20%)'
    },
    selectContainer: {
        width: '100%',
    },
    button: {
        padding: '12px 25px',
        border: 'none',
        borderRadius: '5px',
        backgroundColor: '#28a745',
        color: 'white',
        cursor: 'pointer',
        fontSize: '16px',
        transition: 'background-color 0.3s ease',
    },
    buttonHover: {
        backgroundColor: '#218838',
    },
};
