import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import roomService from '../../../../services/roomService';
// import timeSlotService from '../../../../services/timeSlotService';  // Assuming you have this service for fetching time slots
import { styles } from './styles';

function SessionForm({ rooms }) {
    const [selectedRoom, setSelectedRoom] = useState(null);
    const [timeSlots, setTimeSlots] = useState([]);

    const handleRoomChange = async (selectedOption) => {
        setSelectedRoom(selectedOption);
        const data = await timeSlotService.getTimeSlotsByRoomId(selectedOption.value);  // Fetch time slots based on selected room
        setTimeSlots(data);
    };

    const handleCreateSession = (e) => {
        e.preventDefault();
        // Handle session creation logic here
    };

    const roomOptions = rooms.map(room => ({ value: room.id, label: room.name }));

    return (
        <form style={styles.roomListForm} onSubmit={handleCreateSession}>
            <Select 
                options={roomOptions} 
                onChange={handleRoomChange} 
                value={selectedRoom}
            />
            <button type="submit" style={styles.roomListButton}>Create</button>
            <ul>
                {timeSlots.map(slot => (
                    <li key={slot.id}>{slot.time}</li>  // Assuming each slot has an id and time
                ))}
            </ul>
        </form>
    );
}

export default RoomForm;
