import React, { useState, useEffect } from 'react';
import roomService from '../../../../services/roomService';
import { styles } from './styles';
import TopBar from '../../TopBar/TopBar';
import { Link } from "react-router-dom";
import Select from 'react-select';
import SessionForm from '../SessionForm/SessionForm'
import movieService from '../../../../services/movieService';

function SessionList() {
    const [rooms, setRooms] = useState([]);
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        async function fetchRooms() {
            const data = await roomService.getAllRooms();
            setRooms(data);
        }
        fetchRooms();
    }, []);


    useEffect(() => {
        async function fetchMoovies() {
            const data = await movieService.getAllRooms();
            setMovies(data);
        }
        fetchMoovies();
    }, []);

    const handleCreateSession = () => {

    }

    return (
        <div>
            <TopBar />

            <div style={styles.roomListContainer}>
                <div style={styles.roomListHeader}>
                    <h2>Rooms</h2>
                    <SessionForm rooms={rooms} movies={movies} />
                </div>
                <ul>
                    {rooms.map(room => (
                        <li key={room.id} style={styles.roomListItem}>
                            <Link to={`/admin/rooms/${room.id}`}>{room.name}</Link>
                            {/* <button onClick={() => handleDeleteRoom(room.id)} style={styles.deleteButton}>Delete</button> */}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default SessionList;
