import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api/rooms/';

const roomService = {
    getAllRooms: async () => {
        const response = await axios.get(API_BASE_URL);
        return response.data;
    },

    getRoomById: async (roomId) => {
        const response = await axios.get(`${API_BASE_URL}${roomId}`);
        return response.data;
    },

    createRoom: async (data) => {
        const response = await axios.post(API_BASE_URL, data);
        return response.data;
    },

    updateRoom: async (roomId, data) => {
        const response = await axios.put(`${API_BASE_URL}${roomId}`, data);
        return response.data;
    },

    deleteRoom: async (roomId) => {
        await axios.delete(`${API_BASE_URL}${roomId}`);
    },

    getTimeSlotsByRoomId: async (roomId) => {
        const response = await axios.get(`${API_BASE_URL}${roomId}/time-slots`);
        return response.data;
    },

    addTimeSlot: async (roomId, timeSlotData) => {
        const response = await axios.post(`${API_BASE_URL}${roomId}/time-slots`, timeSlotData);
        return response.data;
    },

    updateTimeSlot: async (roomId, timeSlotId, updatedTimeSlotData) => {
        const response = await axios.put(`${API_BASE_URL}${roomId}/time-slots/${timeSlotId}`, updatedTimeSlotData);
        return response.data;
    },
    deleteTimeSlot: async (roomId, timeSlotId) => {
        await axios.delete(`${API_BASE_URL}${roomId}/time-slots/${timeSlotId}`);
    },
    getTimeSlotsByRoomId: async(roomId) => {
        const response = await axios.get(`${API_BASE_URL}${roomId}/time-slots`);
        return response.data;
    }
};

export default roomService;
