const rooms = [
    { id: 1, name: 'Living Room' },
    { id: 2, name: 'Bedroom' },
    { id: 3, name: 'Kitchen' }
  ];

  function getAllRooms() {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve(rooms);
      }, 500);
    });
  }

  function getRoomById(id) {
    return new Promise((resolve) => {
      setTimeout(() => {
        const room = rooms.find(r => r.id === parseInt(id));
        resolve(room);
      }, 500);
    });
  }

  function createRoom(data) {
    return new Promise((resolve) => {
      setTimeout(() => {
        const newRoom = { id: rooms.length + 1, ...data };
        rooms.push(newRoom);
        resolve(newRoom);
      }, 500);
    });
  }

  function updateRoom(id, data) {
    return new Promise((resolve) => {
      setTimeout(() => {
        let roomToUpdate = rooms.find(r => r.id === parseInt(id));
        roomToUpdate.name = data.name;
        resolve(roomToUpdate);
      }, 500);
    });
  }

  function deleteRoom(id) {
    return new Promise((resolve) => {
      setTimeout(() => {
        const index = rooms.findIndex(r => r.id === parseInt(id));
        rooms.splice(index, 1);
        resolve();
      }, 500);
    });
  }

  const roomService = {
    getAllRooms,
    getRoomById,
    createRoom,
    updateRoom,
    deleteRoom
  };

  export default roomService;
