// SelectFilm.js
import React from 'react';
import { styles } from './styles';

const SelectFilm = ({ setFilm, prevStep, nextStep }) => {
    const handleFilmSelection = (film) => {
        setFilm(film);
        nextStep();
    };

    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        async function fetchRooms() {
            try {
                const data = await roomService.getAllRooms();
                setRooms(data);
            } catch (error) {
                console.error('Error fetching rooms:', error);
            }
        }
        fetchRooms();
    }, []);


    return (
        <div style={styles.selectFilmContainer}>
            <h2>Select a Film</h2>
            <button style={styles.button} onClick={() => handleFilmSelection('Film 1')}>Film 1</button>
            <button style={styles.button} onClick={() => handleFilmSelection('Film 2')}>Film 2</button>
            <button style={styles.backButton} onClick={prevStep}>Back</button>
        </div>
    );
};

export default SelectFilm;
