// services/seatService.js
import axios from 'axios';

const seatService = {
    getAvailableSeats: async (roomId, timeSlotId) => {
        const response = await axios.get(`/api/rooms/${roomId}/timeslots/${timeSlotId}/seats`);
        return response.data;
    },
    bookSeat: async (data) => {
        const response = await axios.post(`/api/seats/book`, data);
        return response.data;
    },
};

export default seatService;
