import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import roomService from '../../services/roomService'; // Import your room service
import { styles } from './styles'; // Import styles object

const Home = () => {
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        async function fetchRooms() {
            try {
                const data = await roomService.getAllRooms();
                setRooms(data);
            } catch (error) {
                console.error('Error fetching rooms:', error);
                // Handle error state or alert the user
            }
        }
        fetchRooms();
    }, []);

    return (
        <div style={styles.container}>
            <h1 style={styles.heading}>Welcome to Cinema App</h1>
            <p style={styles.paragraph}>The most populated cinema application</p>
            <p style={styles.paragraph}>Choose a room and a film</p>

            <div style={styles.roomsContainer}>
                <h2>Available Rooms:</h2>
                {rooms.length === 0 ? (
                    <p>Loading rooms...</p>
                ) : (
                    <ul style={styles.roomsList}>
                        {rooms.map(room => (
                            <li key={room.id} style={styles.roomItem}>
                                <Link to={`/room/${room.id}`} style={styles.roomLink}>
                                    <div style={styles.roomInfo}>
                                        <strong>Room Name:</strong> {room.name}<br />
                                        <strong>Capacity:</strong> {room.capacity}<br />
                                        {/* <strong>Location:</strong> {room.location}<br /> */}
                                        {/* <strong>Features:</strong> {room.features.join(', ')}<br /> */}
                                        {/* Add more room details as needed */}
                                    </div>
                                </Link>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default Home;
