import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import roomService from '../../../../services/roomService';
// import { styles } from './styles';

function SessionForm({ rooms }) {
    const [selectedRoom, setSelectedRoom] = useState(null);
    const [timeSlots, setTimeSlots] = useState([]);

    const handleRoomChange = async (selectedOption) => {
        setSelectedRoom(selectedOption);
        const data = await roomService.getTimeSlotsByRoomId(selectedOption.value);
        setTimeSlots(data);
    };

    const handleCreateSession = (e) => {
        e.preventDefault();
    };

    const roomOptions = rooms.map(room => ({ value: room.id, label: room.name }));
    const timeSlotsOptions = timeSlots.map(slot => ({ value: slot.id, label: slot.start_time}));

    return (
        <form  onSubmit={handleCreateSession}>
            <Select 
                options={roomOptions} 
                onChange={handleRoomChange} 
                value={selectedRoom}
            />
            <Select 
                options={timeSlots} 
            />
            <button type="submit">Create</button>
            {/* <ul>
                {timeSlots.map(slot => (
                    <li key={slot.id}>{slot.time}</li>  // Assuming each slot has an id and time
                ))}
            </ul> */}
        </form>
    );
}

export default SessionForm;
