import React from 'react';
import { styles } from './styles';
import { Link } from "react-router-dom";

const SelectRoom = ({ setRoom, nextStep, rooms }) => {
    const handleRoomSelection = (room) => {
        setRoom(room);
        nextStep();
    };

    return (
        <div>
            <h2>Select a Room</h2>
            {/* <button onClick={() => handleRoomSelection('Room 1')}>Room 1</button>
            <button onClick={() => handleRoomSelection('Room 2')}>Room 2</button> */}
            {rooms.length === 0 ? (
                <p>Loading rooms...</p>
            ) : (
                <ul style={styles.roomsList}>
                    {rooms.map(room => (
                        <Link to={`/room/${room.id}`} style={styles.roomLink}>
                            <li key={room.id} style={styles.roomItem}>
                                <div style={styles.roomInfo}>
                                    <strong>Room Name:</strong> {room.name}<br />
                                </div>
                            </li>
                        </Link>
                    ))}
                </ul>
            )}
        </div>


    );
};

export default SelectRoom;
