import React, { useState } from 'react';
import { styles } from './styles';
import BookingModal from '../BookingModal/BookingModal';

const SeatSelector = ({ room }) => {
    const [selectedSeats, setSelectedSeats] = useState([]);
    const [showModal, setShowModal] = useState(false);
    if (!room) return '';
    const numRows = room.rows;
    const numCols = room.columns;
    const [bookingInfo, setBookingInfo] = useState({ name: '', phoneNumber: '' }); // State for booking information

    const updateSelectedSeats = (seat) => {
        const newSelectedSeats = [...selectedSeats]; // Create a copy
        const seatIndex = newSelectedSeats.findIndex(s => { return s.seatNumber == seat.seatNumber });
        if (seatIndex !== -1) {
            newSelectedSeats.splice(seatIndex, 1);
        } else {
            newSelectedSeats.push(seat);
        }

        console.log(newSelectedSeats)
        setSelectedSeats(newSelectedSeats);
    };

    const handleSeatClick = (seat) => {
        const updateSelection = () => {
            updateSelectedSeats(seat);
        };
        updateSelection();
    }
    // Function to toggle seat availability (for demonstration purposes)
    // const toggleSeatAvailability = (row, col) => {
    //     const updatedAvailability = [...seatAvailability];
    //     updatedAvailability[row][col] = !updatedAvailability[row][col];
    //     setSeatAvailability(updatedAvailability);
    // };

    const handleBookClick = () => {
        // Check if any seats are selected
        if (selectedSeats.length === 0) {
            alert('Please select a seat before booking.');
            return;
        }

        // Open the modal
        setShowModal(true);
    };


    const seatElements = [];

    for (let row = 0; row < numRows; row++) {
        const rowElements = []; // Create an array to hold elements for this row

        for (let col = 0; col < numCols; col++) {
            const seatNumber = row * numCols + col + 1; // Calculate seat number based on row and column
            const seat = { row, col, seatNumber };
            const isAvailable = !selectedSeats.includes(seat);
            const chairStyle = {
                ...styles.seatItem,
                backgroundColor: isAvailable ? '#c0c0c0' : '#808080', // Grey for available, darker grey for unavailable

                cursor: isAvailable ? 'pointer' : 'not-allowed',
            };

            rowElements.push(
                <div
                    key={`${row}-${col}`}
                    style={chairStyle}
                    onClick={() => handleSeatClick(seat)}
                >
                    {seatNumber}
                </div>
            );
        }

        // Push the row container with all seat elements into seatElements array
        seatElements.push(
            <div key={`row-${row}`} style={{ display: 'flex', justifyContent: 'center' }}>
                {rowElements}
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Select a Seat</h2>
            <div style={styles.seatRow}>
                {seatElements}
            </div>

            <button onClick={handleBookClick} disabled={selectedSeats.length === 0}>Book Selected Seats</button>
            <BookingModal showModal={ showModal } />
        </div>
    );
};

export default SeatSelector;
