import React, { useState, useEffect } from 'react';
import { styles } from './styles'; // Import styles object
import roomService from '../../services/roomService'; // Import your room service

const Home = () => {
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        async function fetchRooms() {
            const data = await roomService.getAllRooms();
            setRooms(data);
        }
        fetchRooms();
    }, []);

    return (
        <div style={styles.container}>
            <h1 style={styles.heading}>Welcome to Cinema App</h1>
            <p style={styles.paragraph}>The most populated cinema application</p>
            <p style={styles.paragraph}>Choose a room and a film</p>

            <div>
                <h2>Available Rooms:</h2>
                <ul>
                    {rooms.map(room => (
                        <li key={room.id}>
                            <strong>Room Name:</strong> {room.name}<br />
                            <strong>Capacity:</strong> {room.capacity}<br />
                            {/* Add more room details as needed */}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Home;
