import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap'; // Import Bootstrap components
import { styles } from './styles';

const SeatSelector = ({ room }) => {
    const [selectedSeats, setSelectedSeats] = useState([]);
    const [showModal, setShowModal] = useState(false); // State for modal visibility
    const [bookingInfo, setBookingInfo] = useState({ name: '', phoneNumber: '' }); // State for booking information

    if (!room) return '';

    const numRows = room.rows;
    const numCols = room.columns;

    const updateSelectedSeats = (seat) => {
        const newSelectedSeats = [...selectedSeats]; // Create a copy
        const seatIndex = newSelectedSeats.findIndex(s => s.seatNumber === seat.seatNumber);
        if (seatIndex !== -1) {
            newSelectedSeats.splice(seatIndex, 1);
        } else {
            newSelectedSeats.push(seat);
        }

        setSelectedSeats(newSelectedSeats);
    };

    const handleSeatClick = (seat) => {
        updateSelectedSeats(seat);
    };

    const handleBookClick = () => {
        // Check if any seats are selected
        if (selectedSeats.length === 0) {
            alert('Please select a seat before booking.');
            return;
        }

        // Open the modal
        setShowModal(true);
    };

    const handleBookingSubmit = (event) => {
        event.preventDefault(); // Prevent default form submission behavior

        const { name, phoneNumber } = bookingInfo;

        // Perform booking logic here (e.g., send data to server, display confirmation)
        console.log('Booking information:', { name, phoneNumber, selectedSeats }); // For demonstration purposes

        // Clear booking information and close modal
        setBookingInfo({ name: '', phoneNumber: '' });
        setShowModal(false);
    };

    const handleBookingInputChange = (event) => {
        setBookingInfo({ ...bookingInfo, [event.target.name]: event.target.value });
    };

    const seatElements = [];

    for (let row = 0; row < numRows; row++) {
        const rowElements = [];

        for (let col = 0; col < numCols; col++) {
            const seatNumber = row * numCols + col + 1;
            const seat = { row, col, seatNumber };
            const isAvailable = !selectedSeats.includes(seat);
            const chairStyle = {
                ...styles.seatItem,
                backgroundColor: isAvailable ? '#c0c0c0' : '#808080',
                cursor: isAvailable ? 'pointer' : 'not-allowed',
            };

            rowElements.push(
                <div
                    key={`<span class="math-inline">\{row\}\-</span>{col}`}
                    style={chairStyle}
                    onClick={() => handleSeatClick(seat)}
                >
                    {seatNumber}
                </div>
            );
        }

        seatElements.push(
            <div key={`row-${row}`} style={{ display: 'flex', justifyContent: 'center' }}>
                {rowElements}
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Select a Seat</h2>
            <div style={styles.seatRow}>
                {seatElements}
            </div>
            <Button variant="primary" onClick={handleBookClick} disabled={selectedSeats.length === 0}>
                Book Selected Seats
            </Button>

            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Booking Information</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleBookingSubmit}>
                        <div className="form-group">
                            <label htmlFor="name">Name:</label>
                            <input
                                type="text"
                                className="form-control"
                                id="name"
                                name="name"
                                value={bookingInfo.name}
                                onChange={handleBookingInputChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="phoneNumber">Phone Number:</label>
                            <input
                                type="tel" // Set type to "tel" for phone number input
                                className="form-control"
                                id="phoneNumber"
                                name="phoneNumber"
                                value={bookingInfo.phoneNumber}
                                onChange={handleBookingInputChange}
                                required
                            />
                        </div>
                        <Button variant="primary" type="submit">
                            Submit Booking
                        </Button>
                    </form>
                </Modal.Body>
            </Modal>
        </div>
    );
};

export default SeatSelector;
