import React, { useState, useEffect } from 'react';
import movieService from '../../../../services/movieService';
import { styles } from './styles';
import TopBar from '../../TopBar/TopBar';

function MovieList() {
    const [movies, setMovies] = useState([]);
    const [newMovieName, setNewMovieName] = useState('');
    const [newMoviePosterFile, setNewMoviePosterFile] = useState(null); // State for file input

    useEffect(() => {
        async function fetchMovies() {
            const data = await movieService.getAllMovies();
            setMovies(data);
        }
        fetchMovies();
    }, []);

    const handleCreateMovie = async (e) => {
        e.preventDefault();
        if (newMovieName.trim() === '' || !newMoviePosterFile) return;

        // Upload poster file
        const formData = new FormData();
        formData.append('name', newMovieName);
        formData.append('poster', newMoviePosterFile);

        const newMovie = await movieService.createMovie(formData);
        setMovies([...movies, newMovie]);
        setNewMovieName('');
        setNewMoviePosterFile(null);
    };

    const handleDeleteMovie = async (id) => {
        const isConfirmed = window.confirm('Are you sure you want to delete this movie?');
        if (!isConfirmed) return;

        await movieService.deleteMovie(id);
        setMovies(movies.filter(movie => movie.id !== id));
    };

    const handlePosterFileChange = (e) => {
        const file = e.target.files[0];
        setNewMoviePosterFile(file);
    };

    return (
        <div>
            <TopBar />

            <div style={styles.movieListContainer}>
                <div style={styles.movieListHeader}>
                    <h2>Movies</h2>
                    <form style={styles.movieListForm} onSubmit={handleCreateMovie}>
                        <input
                            type="text"
                            value={newMovieName}
                            onChange={(e) => setNewMovieName(e.target.value)}
                            style={styles.movieListInput}
                            placeholder="Enter movie name"
                            required
                        />
                        <input
                            type="file"
                            onChange={handlePosterFileChange}
                            style={styles.movieListInput}
                            accept="image/*"
                            required
                        />
                        <button type="submit" style={styles.movieListButton}>Create</button>
                    </form>
                </div>
                <ul>
                    {movies.map(movie => (
                        <li key={movie.id} style={styles.movieListItem}>
                            <div>
                                <img src={movie.poster} alt={`${movie.name} poster`} style={styles.posterImage} />
                                <span>{movie.name}</span>
                            </div>
                            <button onClick={() => handleDeleteMovie(movie.id)} style={styles.deleteButton}>Delete</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default MovieList;
