import React, { useState, useEffect } from 'react';
import SelectRoom from '../SelectRoom/SelectRoom';
import SelectMovie from '../SelectMovie/SelectMovie';
import SelectTimeSlot from '../SelectTimeSlot/SelectTimeSlot';
import SelectSeat from '../SelectSeat/SelectSeat';
import { styles } from './styles';
import roomService from '../../../services/roomService';
import movieService from '../../../services/movieService';

const BookingProcess = () => {
    const [step, setStep] = useState(1);
    const [room, setRoom] = useState(null);
    const [film, setFilm] = useState(null);
    const [timeSlot, setTimeSlot] = useState(null);
    const [bookingInfo, setBookingInfo] = useState({ name: '', phoneNumber: '' }); // State for booking information

    const nextStep = () => setStep(step + 1);
    const prevStep = () => setStep(step - 1);

    const [rooms, setRooms] = useState([]);
    const [movies, setMovies] = useState([]);

    async function fetchRooms() {
        try {
            const data = await roomService.getAllRooms();
            setRooms(data);
        } catch (error) {
            console.error('Error fetching rooms:', error);
        }
    }

    const handleBookingSubmit = (event) => {
        event.preventDefault(); // Prevent default form submission behavior

        const { name, phoneNumber } = bookingInfo;

        // Perform booking logic here (e.g., send data to server, display confirmation)
        console.log('Booking information:', { name, phoneNumber, selectedSeats }); // For demonstration purposes

        // Clear booking information and close modal
        setBookingInfo({ name: '', phoneNumber: '' });
        setShowModal(false);
    };

    const handleBookingInputChange = (event) => {
        setBookingInfo({ ...bookingInfo, [event.target.name]: event.target.value });
    };


    const fetchMovies = async () => {
        try {
            const data = await movieService.getAllMovies();
            setMovies(data);
        } catch (error) {
            console.error('Error fetching movies:', error);
        }
    };

    useEffect(() => {
        fetchRooms();
        fetchMovies();
    }, []);

    const goBack = () => {
        if (step > 1) {
            setStep(step - 1);
        }
    }

    const changeStep = (i) => {
        setStep(i);
    }

    return (
        <div style={styles.bookingProcess}>
            <div style={styles.stepsIndicator}>
                <div style={styles.step} onClick={goBack}>Go back</div>
                <div style={{ ...styles.step, ...(step === 1 ? styles.activeStep : {}) }} onClick={() => changeStep(1)}>Step 1: Select Room</div>
                <div style={{ ...styles.step, ...(step === 2 ? styles.activeStep : {}) }} onClick={() => changeStep(2)}>Step 2: Select Film</div>
                <div style={{ ...styles.step, ...(step === 3 ? styles.activeStep : {}) }} onClick={() => changeStep(3)}>Step 3: Select Time Slot</div>
                <div style={{ ...styles.step, ...(step === 4 ? styles.activeStep : {}) }} onClick={() => changeStep(4)}>Step 4: Pick The Seat</div>
            </div>
            {step === 1 && <SelectRoom setRoom={setRoom} nextStep={nextStep} rooms={rooms} />}
            {step === 2 && <SelectMovie setFilm={setFilm} prevStep={prevStep} nextStep={nextStep} movies={movies} />}
            {step === 3 && <SelectTimeSlot setTimeSlot={setTimeSlot} nextStep={nextStep} room={room} film={film} />}
            {step === 4 && <SelectSeat room={room} selectedSeats={selectedSeats} setShowModal={setShowModal}/>}
        </div>
    );
};

export default BookingProcess;
