import React, { useEffect, useState } from 'react';
import timeSlotService from '../../../services/timeSlotService';

const SelectTimeSlot = ({ setTimeSlot, prevStep, room, film }) => {
    const [timeSlots, setTimeSlots] = useState([]);

    useEffect(() => {
        if (room && film) {
            fetchTimeSlots(room.id, film.id);
        }
    }, [room, film]);

    const fetchTimeSlots = async (roomId, filmId) => {
        try {
            const data = await timeSlotService.getTimeSlots(roomId, filmId);
            setTimeSlots(data);
        } catch (error) {
            console.error('Error fetching time slots:', error);
            setTimeSlots([]);
        }
    };

    const handleTimeSlotSelection = (timeSlot) => {
        setTimeSlot(timeSlot);
    };

    return (
        <div>
            <h2>Select a Time Slot</h2>
            {timeSlots.length === 0 ? (
                <p>Loading time slots...</p>
            ) : (
                <div>
                    {timeSlots.map((timeSlot) => (
                        <button key={timeSlot.id} onClick={() => handleTimeSlotSelection(timeSlot.time)}>
                            {timeSlot.time}
                        </button>
                    ))}
                </div>
            )}
            <button onClick={prevStep}>Back</button>
        </div>
    );
};

export default SelectTimeSlot;
