// services/seatService.js
import axios from 'axios';
const API_BASE_URL = 'http://localhost:8000/api/books/';

const seatService = {
    getAvailableSeats: async (roomId, timeSlotId) => {
        const response = await axios.get(`${API_BASE_URL}/rooms/${roomId}/timeslots/${timeSlotId}/seats`);
        return response.data;
    },
    bookSeat: async (data) => {
        const response = await axios.post(`${API_BASE_URL}/book`, data);
        return response.data;
    },
};

export default seatService;
