import React, { useEffect, useState } from 'react';
import axios from 'axios';
import FilmSelectionComponent from './FilmSelectionComponent';

const SessionList = ({ roomId }) => {
    const [sessions, setSessions] = useState([]);
    const [selectedFilm, setSelectedFilm] = useState('');

    useEffect(() => {
        if (roomId) {
            axios.get(`http://localhost:5000/sessions/${roomId}`)
                .then(response => setSessions(response.data))
                .catch(error => console.error('Error fetching sessions:', error));
        }
    }, [roomId]);

    const handleAddSession = () => {
        const newSession = { roomId, film: selectedFilm };
        axios.post('http://localhost:5000/sessions', newSession)
            .then(response => setSessions([...sessions, response.data]))
            .catch(error => console.error('Error adding session:', error));
    };

    const handleDeleteSession = (sessionId) => {
        axios.delete(`http://localhost:5000/sessions/${sessionId}`)
            .then(() => setSessions(sessions.filter(session => session.id !== sessionId)))
            .catch(error => console.error('Error deleting session:', error));
    };

    return (
        <div>
            <h2>Sessions for Room {roomId}</h2>
            <ul>
                {sessions.map(session => (
                    <li key={session.id}>
                        {session.film}
                        <button onClick={() => handleDeleteSession(session.id)}>Delete</button>
                    </li>
                ))}
            </ul>
            <FilmSelectionComponent onSelectFilm={setSelectedFilm} />
            <button onClick={handleAddSession}>Add Session</button>
        </div>
    );
};

export default SessionList;
