import React, { useState } from 'react';
import { styles } from './styles';

const SeatSelector = ({ seats }) => {
    const numRows = 6; // Number of rows
    const numCols = 8;
    const [seatAvailability, setSeatAvailability] = useState(Array(6).fill(Array(8).fill(true))); // Initially all seats are available
    const [selectedSeat, setSelectedSeat] = useState(null); // State to track selected seat
    const [isModalOpen, setIsModalOpen] = useState(false); // State to control modal visibility
    const [bookingDetails, setBookingDetails] = useState({ name: '', phone: '' }); // State to manage booking details

    // Function to toggle seat availability and set selected seat
    const toggleSeatAvailability = (row, col) => {
        const updatedAvailability = [...seatAvailability];
        updatedAvailability[row][col] = !updatedAvailability[row][col];
        setSeatAvailability(updatedAvailability);
        setSelectedSeat({ row, col });
    };

    // Function to handle seat click
    const handleSeatClick = (seat) => {
        if (seatAvailability[seat.row][seat.col]) {
            toggleSeatAvailability(seat.row, seat.col);
        }
    };

    // Function to handle booking
    const handleBook = () => {
        setIsModalOpen(true); // Open modal
    };

    // Function to handle modal form submission
    const handleModalSubmit = (e) => {
        e.preventDefault();
        // Implement your booking logic here (e.g., API call to book the seat)
        console.log('Booking details:', bookingDetails);
        // Reset state after booking
        setBookingDetails({ name: '', phone: '' });
        setIsModalOpen(false);
        // You may want to update seat status after booking, e.g., set it as unavailable
    };

    // Update booking details state
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setBookingDetails({ ...bookingDetails, [name]: value });
    };

    const seatElements = [];

    for (let row = 0; row < numRows; row++) {
        const rowElements = [];

        for (let col = 0; col < numCols; col++) {
            const seatNumber = row * numCols + col + 1;
            const isAvailable = seatAvailability[row][col];

            const chairStyle = {
                ...styles.seatItem,
                backgroundColor: isAvailable ? '#c0c0c0' : '#808080',
                borderRadius: '50%',
                width: '40px',
                height: '40px',
                margin: '5px',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                cursor: isAvailable ? 'pointer' : 'not-allowed',
                border: selectedSeat?.row === row && selectedSeat?.col === col ? '2px solid orange' : '2px solid transparent',
            };

            rowElements.push(
                <div
                    key={`${row}-${col}`}
                    style={chairStyle}
                    onClick={() => handleSeatClick({ row, col })}
                >
                    {seatNumber}
                </div>
            );
        }

        seatElements.push(
            <div key={`row-${row}`} style={{ display: 'flex', justifyContent: 'center' }}>
                {rowElements}
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Select a Seat</h2>
            <div style={styles.seatRow}>
                {seatElements}
            </div>
            <button onClick={handleBook}>Book</button>

            {/* Modal for entering name and phone */}
            {isModalOpen && (
                <div style={styles.modal}>
                    <form onSubmit={handleModalSubmit}>
                        <label>
                            Name:
                            <input type="text" name="name" value={bookingDetails.name} onChange={handleInputChange} />
                        </label>
                        <label>
                            Phone:
                            <input type="text" name="phone" value={bookingDetails.phone} onChange={handleInputChange} />
                        </label>
                        <button type="submit">Submit</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default SeatSelector;
