import React, { useEffect, useState } from 'react';
import seatService from '../../../services/seatService';
import { styles } from './styles';

const SeatSelector = ({ seats, selectedSeat, handleSeatClick }) => {
    const seatElements = [];
    let x = 6;
    let y = 8;

    for (let row = 0; row < x; row++) { // 6 rows
        for (let col = 0; col < y; col++) { // 8 columns
            const seat = { row, col };
            seatElements.push(
                <div
                    key={`${row}-${col}`}
                    style={{
                        ...styles.seatItem,
                        backgroundColor: 'green',
                        borderColor: selectedSeat?.row === row && selectedSeat?.col === col ? 'blue' : 'transparent',
                    }}
                    onClick={() => handleSeatClick(seat)}
                >
                    {`${row}-${col}`}
                </div>
            );
        }
    }


    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Select a Seat</h2>
            {/* {seats.length === 0 ? (
                <p style={styles.noSeatsMessage}>No available seat was found</p>
            ) : ( */}
                <div style={styles.seatGrid}>
                    {seatElements}
                </div>
            {/* )} */}
        </div>
    );
};

export default SeatSelector;
